import React, { useState } from 'react';
import './Card.css';
import more from './more.png';
import { Link } from 'react-router-dom';
import { supabase } from '../client';

const Card = (props) => {
  // Initialize the count with props.upvotes or default to 0 if not available
  const [count, setCount] = useState(props.upvotes || 0);

  const updateCount = async (event) => {
    event.preventDefault();

    // Attempt to update the upvotes in the database and fetch the updated value
    const { data, error } = await supabase
      .from('criteria')
      .update({ upvotes: count + 1 })
      .eq('id', props.id)
      .single();  // Retrieves the updated row to ensure synchronization

    if (data) {
      // Update the local state to reflect the updated count from the database
      setCount(data.upvotes);
    } else if (error) {
      // Log any errors encountered during the update
      console.error('Failed to update upvotes:', error);
    }
  };

  return (
      <div className="Card">
          <Link to={'edit/'+ props.id}><img className="moreButton" alt="edit button" src={more} /></Link>
          <h2 className="title">{props.title}</h2>
          <h3 className="subtopic">{"Subtopic: " + props.subtopic}</h3>
          <p className="content">{"Content: " + props.content}</p>
          <button className="upvoteButton" onClick={updateCount}>
            Upvotes ⬆️ {count}
          </button>
      </div>
  );
};

export default Card;
