import React, { useState } from 'react';
import './CreatePost.css';
import { supabase } from '../client';

const CreatePost = () => {
    const [post, setPost] = useState({ name: "", author: "", description: "" });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    const createPost = async (event) => {
        event.preventDefault();

        await supabase
            .from('Posts')
            .insert([
                { name: post.name, author: post.author, description: post.description }
            ])
            .select()

        window.location.href = "/"; 
    };

    return (
        <div>
            <form onSubmit={createPost}> {}
                <label htmlFor="name">Name</label> <br />
                <input type="text" id="name" name="name" onChange={handleChange} /><br /><br/>

                <label htmlFor="author">Author</label><br />
                <input type="text" id="author" name="author" onChange={handleChange} /><br /><br/>

                <label htmlFor="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" name="description" onChange={handleChange} />
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    );
};

export default CreatePost;
